$(function(){$("<div class='float'></div>").appendTo('.floating');$('.float').css({'position':'absolute','z-index':'-1','-webkit-transition':'all 0.3s ease','-moz-transition':'all 0.3s ease','transition':'all 0.3s ease','display':'none'});$('.floating').css('position','relative');$('.floating').on("mouseenter",function(){$('.float',this).css('display','block')}).on("mouseleave",function(){$('.float',this).css('display','none')});$('.floating li').on("mouseenter",function(){$('.float').css({'width':$(this).width()+parseInt($(this).css('padding-left'))+parseInt($(this).css('padding-right')), 'height':$(this).height()+parseInt($(this).css('padding-top'))+parseInt($(this).css('padding-bottom')),'top':$(this).position().top,'left':$(this).position().left})})})